dt-api.example:
var table = $('#example').DataTable( {
	colReorder: true
} );

$('#reverse').click( function (e) {
	table.colReorder.order( [ 5, 4, 3, 2, 1, 0 ] );
} );

var table = $('#example').DataTable( {
	colReorder: true
} );

$('#reverse').click( function (e) {
	table.colReorder.order( [ 0, 1, 2, 3, 4, 5 ], true );
} );
